children.css:
@import url("https://fonts.googleapis.com/css?family=Open+Sans|Sacramento");
html {
  font-size: 14.5px;
}

.body {
  font-family: "Open Sans", sans-serif;
  line-height: 1.45em;
  background-color: #eaebeb;
  color: #404646;
padding:1em;
}

* {
  box-sizing: border-box;
}

.main-container {
  margin: 1.6rem 0.8rem;
}

.grid-container {
  margin: auto;
  display: grid;
  grid: auto-flow 12.8rem/repeat(auto-fit, 12.8rem);
  grid-gap: 1.1312rem;
  grid-auto-flow: dense;
  justify-content: center;
  max-width: 75em;
}

.card {
  grid-row: auto/span 1;
  grid-column: auto/span 1;
  background-color: white;
  box-shadow: 1px 3px 3px rgba(0, 10, 20, 0.06);
}
.card h1, .card h2, .card h3, .card h4, .card p {
  margin-top: 0;
  font-weight: normal;
}
.card__icon {
  color: #404646;
}
.card__icon--large {
  font-size: 3.5em;
}
.card__image {
  height: 100%;
  max-height: 100%;
  width: 100%;
  display: flex;
}
.card__image img {
  display: block;
  height: 100%;
  min-height: 100%;
  max-height: 100%;
  width: 100%;
  -o-object-fit: cover;
     object-fit: cover;
}
.card__top-to-bottom {
  height: 100%;
  width: 100%;
  display: grid;
  grid: 1fr 1fr/1fr;
}
.card__top-to-bottom img {
  min-height: auto;
}
.card__side-by-side {
  height: 100%;
  width: 100%;
  display: grid;
  grid: 1fr/9fr 8fr;
}
.card__side-by-side--m {
  height: 100%;
  width: 100%;
  display: grid;
  grid: 1fr 1fr/1fr;
}
.card__side-by-side--m img {
  min-height: auto;
}
.card__content {
  padding: 1.6rem;
}
.card__content hr {
  max-width: 3.2rem;
  border: 1px solid #e077b0;
  margin: 1.6rem;
}
.card__content--has-icon {
  height: 100%;
  width: 100%;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-around;
  align-items: center;
  text-align: center;
}
.card__content--has-icon p {
  margin: 0;
}
.card__button {
  width: 12.8rem;
  margin: 2.2624rem 0;
  padding: 0.8rem 0.8rem 0.8rem 2.2624rem;
  text-align: center;
  text-transform: uppercase;
  background: none;
  border: 0.5px solid #777;
  border-radius: 2px;
  -webkit-user-select: none;
     -moz-user-select: none;
      -ms-user-select: none;
          user-select: none;
}
.card__button i {
  margin-left: 1.1312rem;
  vertical-align: -8%;
  font-size: 1.25em;
}
.card__button:hover {
  border-color: #e077b0;
}
.card--featured {
  grid-row: auto/span 3;
  grid-column: auto/span 2;
}
.card--2x2 {
  grid-row: auto/span 2;
  grid-column: auto/span 2;
}
.card--2x3 {
  grid-row: auto/span 3;
  grid-column: auto/span 2;
}
.card--3x2 {
  grid-row: auto/span 2;
  grid-column: auto/span 3;
}
.card--vertical {
  grid-row: auto/span 2;
}
.card--horizontal {
  grid-column: auto/span 2;
}
.card--frameless {
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: transparent;
  box-shadow: none;
}

.padding-mlarge {
  padding: 2.2624rem;
}
.padding-mlarge--l {
  padding: 1.6rem;
}
.padding-large {
  padding: 3.2rem;
}
.padding-large--l {
  padding: 1.6rem;
}

.big-script {
  height: 100%;
  display: flex;
  flex-flow: column nowrap;
  justify-content: center;
  align-items: center;
  font-family: "Sacramento", sans-serif;
  font-size: 4.3em;
  line-height: 1.15em;
  text-align: center;
}
.big-script p {
  margin: 0;
}

@media (min-width: 627px) {
  .grid-container {
    grid-gap: 1.6rem;
  }

  .card__side-by-side--m {
    display: grid;
    grid: 1fr/9fr 8fr;
  }
  .card__side-by-side--m img {
    min-height: 100%;
  }
  .card--featured {
    grid-row: auto/span 2;
    grid-column: 1/-1;
  }
}
@media (min-width: 859px) {
  .padding-mlarge--l {
    padding: 2.2624rem;
  }

  .padding-large--l {
    padding: 3.2rem;
  }
}
